import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import { Card, CardContent, Typography, CardMedia } from "@mui/material";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Programs.css";

const cardsData = [
  { id: 1, title: "Program 1", desc: "Description 1", image: "https://source.unsplash.com/400x250/?nature,water" },
  { id: 2, title: "Program 2", desc: "Description 2", image: "https://source.unsplash.com/400x250/?nature,forest" },
  { id: 3, title: "Program 3", desc: "Description 3", image: "https://source.unsplash.com/400x250/?nature,mountain" },
  { id: 4, title: "Program 4", desc: "Description 4", image: "https://source.unsplash.com/400x250/?nature,beach" },
];

const Programs = () => {
  // Track window width to apply desktop/tablet/mobile rules
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  // Update window width on resize
  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // React Slick slider settings
  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 4,  // Desktop: show 4 cards
    slidesToScroll: 1,
    centerMode: false,
    autoplay: true,
    autoplaySpeed: 2500,
    dots: true,
    responsive: [
      {
        breakpoint: 1024,  // Tablet
        settings: {
          slidesToShow: 2, // Show 2 cards on tablet
        },
      },
      {
        breakpoint: 768,   // Mobile
        settings: {
          slidesToShow: 1, // Show 1 card on mobile
        },
      },
    ],
  };

  return (
    <div className="programs-section">
      <Slider {...settings} className="programs-slider">
        {cardsData.map((card, index) => {
          let wrapperClass = "program-card-wrapper";

          // Desktop: enlarge middle two cards, keep first & last small
          if (windowWidth >= 1025) {
            if (index === 1 || index === 2) wrapperClass += " center-card";
            else wrapperClass += " small-card";
          }

          return (
            <div key={card.id} className={wrapperClass}>
              <Card className="program-card" elevation={3}>
                <CardMedia
                  component="img"
                  image={card.image}
                  alt={card.title}
                  className="program-card-image"
                />
                <CardContent>
                  <Typography variant="h6">{card.title}</Typography>
                  <Typography variant="body2">{card.desc}</Typography>
                </CardContent>
              </Card>
            </div>
          );
        })}
      </Slider>
    </div>
  );
};

export default Programs;
