import React, { useState } from "react";
import Slider from "react-slick";
import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
} from "@mui/material";
import "./Programs.css";

const Programs = () => {
  // Track the current center index
  const [centerIndex, setCenterIndex] = useState(0);

  // Sample card data
  const cardData = [
    { title: "Card 1", description: "Description for card 1." },
    { title: "Card 2", description: "Description for card 2." },
    { title: "Card 3", description: "Description for card 3." },
    { title: "Card 4", description: "Description for card 4." },
    { title: "Card 5", description: "Description for card 5." },
    { title: "Card 6", description: "Description for card 6." },
  ];

  // Slick carousel settings
  const settings = {
    dots: false,
    infinite: true,
    speed: 600,
    autoplay: true,
    autoplaySpeed: 2000,
    centerMode: true,          // enables "center" highlighting
    centerPadding: "0px",      // no extra padding around center
    slidesToShow: 4,           // desktop: 4 cards
    beforeChange: (_, next) => setCenterIndex(next), // track current slide
    responsive: [
      {
        breakpoint: 1200, // Tablet
        settings: {
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 768, // Mobile
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <div className="cards-container">
      <Slider {...settings}>
        {cardData.map((card, index) => (
          <div key={index}>
            <Card
              className={`card ${
                index === centerIndex ? "large" : ""
              }`} // Apply "large" only to the active center card
            >
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {card.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {card.description}
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="small" sx={{ color: "#d36408" }}>
                  Learn More
                </Button>
              </CardActions>
            </Card>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Programs;
