import React from "react";
import "./NewsEvents.css";

const leftNewsData = [
  {
    title: "News 1",
    content: "This is news content 1",
    date: "2025-09-30",
  },
  {
    title: "News 2",
    content: "This is news content 2",
    date: "2025-09-29",
  },
  {
    title: "News 3",
    content: "This is news content 3",
    date: "2025-09-28",
  },
];

const rightNewsData = [
  {
    title: "News A",
    description: "Description of news A",
    image: "https://via.placeholder.com/150",
  },
  {
    title: "News B",
    description: "Description of news B",
    image: "https://via.placeholder.com/150",
  },
  {
    title: "News C",
    description: "Description of news C",
    image: "https://via.placeholder.com/150",
  },
];

const NewsEvents= () => {
  return (
    <div className="news-container">
      {/* Section 1 */}
      <div className="section section-left">
        <h2 className="section-title">Left Section Title</h2>
        <p className="section-description">
          Small description under left section title.
        </p>
        <div className="cards-container">
          {leftNewsData.map((news, index) => (
            <div className="news-card" key={index}>
              <h4 className="news-card-title">{news.title}</h4>
              <p className="news-card-content">{news.content}</p>
              <div className="news-card-footer">
                <span className="news-card-date">{news.date}</span>
                <button className="read-more-btn">Read More</button>
              </div>
            </div>
          ))}
          <button className="view-all-btn">View All</button>
        </div>
      </div>

      {/* Section 2 */}
      <div className="section section-right">
        <h2 className="section-title">Right Section Title</h2>
        <p className="section-description">
          Small description under right section title.
        </p>
        <div className="scrollable-news">
          {rightNewsData.map((news, index) => (
            <div className="image-news-card" key={index}>
              <img src={news.image} alt={news.title} className="news-image" />
              <h4 className="news-card-title">{news.title}</h4>
              <p className="news-card-description">{news.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default NewsEvents;
