import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Dashboard1() {
  const [user, setUser] = useState(null);
  const [dateFilter, setDateFilter] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true; // prevent setting state on unmounted component

    axios.get("http://localhost:5000/users/profile", { withCredentials: true })
      .then(res => {
        if (isMounted) setUser(res.data.user);
      })
      .catch(err => {
        // Only navigate if we are not already on /login
        if (window.location.pathname !== "/login") {
          navigate("/login");
        }
      });

    return () => { isMounted = false; }; // cleanup
  }, [navigate]);

  const handleLogout = () => {
    axios.post("http://localhost:5000/users/logout", {}, { withCredentials: true })
      .then(() => {
        if (window.location.pathname !== "/login") {
          navigate("/login");
        }
      });
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      {/* Header Section */}
      <div style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: "40px",
        borderBottom: "1px solid #ddd",
        paddingBottom: "10px"
      }}>
        <h1 style={{ margin: 0 }}>Dashboard</h1>

        {/* Date Filter */}
        <div>
          <label htmlFor="dateFilter">Date: </label>
          <input
            type="date"
            id="dateFilter"
            value={dateFilter}
            onChange={(e) => setDateFilter(e.target.value)}
            style={{ padding: "5px" }}
          />
        </div>

        {/* User Info + Logout */}
        <div>
          <span style={{ marginRight: "15px" }}>{user.name} ({user.email})</span>
          <button onClick={handleLogout} style={{ padding: "5px 10px" }}>Logout</button>
        </div>
      </div>

      {/* Welcome Section */}
      <div style={{ textAlign: "center", marginBottom: "30px" }}>
        <h2>Welcome, {user.name}</h2>
      </div>

      {/* Main Content Placeholder */}
      <div style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
        gap: "20px"
      }}>
        <div style={{ padding: "20px", border: "1px solid #ddd", borderRadius: "8px" }}>
          Top Metrics
        </div>
        <div style={{ padding: "20px", border: "1px solid #ddd", borderRadius: "8px" }}>
          Performance Chart
        </div>
      </div>
    </div>
  );
}
