import React, { useEffect, useState } from "react";
import axios from "axios";
import { Navigate } from "react-router-dom";

export default function Dashboard1() {
  const [user, setUser] = useState(null);
  const [dateFilter, setDateFilter] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("http://localhost:5000/users/profile", { withCredentials: true })
      .then(res => setUser(res.data.user))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p>Loading...</p>;
  if (!user) return <Navigate to="/login" />;

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      {/* Header Section */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "40px", borderBottom: "1px solid #ddd", paddingBottom: "10px" }}>
        <h1 style={{ margin: 0 }}>Dashboard</h1>
        <div>
          <label htmlFor="dateFilter">Date: </label>
          <input type="date" id="dateFilter" value={dateFilter} onChange={(e) => setDateFilter(e.target.value)} style={{ padding: "5px" }} />
        </div>
        <div>
          <span style={{ marginRight: "15px" }}>{user.name} ({user.email})</span>
        </div>
      </div>

      <div style={{ textAlign: "center", marginBottom: "30px" }}>
        <h2>Welcome, {user.name}</h2>
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", gap: "20px" }}>
        <div style={{ padding: "20px", border: "1px solid #ddd", borderRadius: "8px" }}>Top Metrics</div>
        <div style={{ padding: "20px", border: "1px solid #ddd", borderRadius: "8px" }}>Performance Chart</div>
      </div>
    </div>
  );
}
