import React from "react";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import { Card, CardContent, Typography, Button, Grid } from "@mui/material";
import "./Programs.css";

const programsData = [
  {
    title: "B. Tech. Marine Engineering",
    description:
      "The Department of Marine Engineering aims to train the best marine engineering graduates for the industry...",
    image: "/images/marine.jpg",
  },
  {
    title: "B. Sc. Nautical Science",
    description:
      "The Department of Nautical Science enables the graduates to become Navigating Officers...",
    image: "/images/nautical.jpg",
  },
  {
    title: "Diploma In Nautical Science",
    description:
      "One year Diploma in Nautical Science (DNS) is a pre-sea training course approved by DGS...",
    image: "/images/dns.jpg",
  },
  {
    title: "Electro Technical Officer",
    description:
      "Our ETO course is your pathway to a dynamic career at sea. Designed to meet industry standards...",
    image: "/images/eto.jpg",
  },
];

export default function Programs() {
  // Slider settings for tablet & mobile
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 2, // default tablet
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768, // mobile
        settings: {
          slidesToShow: 1,
          arrows: true,
        },
      },
    ],
  };

  // Detect screen size for desktop vs carousel
  const isDesktop = window.innerWidth >= 1024;

  return (
    <div className="programs-section">
      <div className="programs-title">
        <Typography variant="h4" gutterBottom>
          Programs That Shape Futures
        </Typography>
        <Typography variant="subtitle1" color="textSecondary">
          From design to technology, our programs deliver measurable success
        </Typography>
      </div>

      {isDesktop ? (
        // Desktop: Grid layout with 4 cards
        <Grid container spacing={3} justifyContent="center">
          {programsData.map((program, index) => (
            <Grid item md={3} key={index}>
              <Card className="program-card">
                <div className="card-content">
                  <img src={program.image} alt={program.title} className="program-img" />
                  <CardContent>
                    <Typography variant="h6" className="program-title">
                      {program.title}
                    </Typography>
                    <Typography variant="body2" className="program-description">
                      {program.description}
                    </Typography>
                    <Button className="read-more">Read More</Button>
                  </CardContent>
                </div>
              </Card>
            </Grid>
          ))}
        </Grid>
      ) : (
        // Tablet & Mobile: Carousel
        <Slider {...settings}>
          {programsData.map((program, index) => (
            <div key={index}>
              <Card className="program-card">
                <div className="card-content">
                  <img src={program.image} alt={program.title} className="program-img" />
                  <CardContent>
                    <Typography variant="h6" className="program-title">
                      {program.title}
                    </Typography>
                    <Typography variant="body2" className="program-description">
                      {program.description}
                    </Typography>
                    <Button className="read-more">Read More</Button>
                  </CardContent>
                </div>
              </Card>
            </div>
          ))}
        </Slider>
      )}
    </div>
  );
}
