import React from "react";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import { Card, CardContent, Typography, Button } from "@mui/material";
import { ArrowBackIos, ArrowForwardIos } from "@mui/icons-material";
import "./Programs.css";

const programsData = [
  {
    title: "B. Tech. Marine Engineering",
    description:
      "The Department of Marine Engineering aims to train the best marine engineering graduates for the industry. Along with providing a broad-based knowledge in theory, the students are exposed to marine equipment with visit to ships and repair yards.....",
    image: "https://via.placeholder.com/150x150?text=Marine",
  },
  {
    title: "B. Sc. Nautical Science",
    description:
      "The Department of Nautical Science enables the graduates to become Navigating Officers on-board on merchant ships plying worldwide. The bachelor program dealing with various theoretical and practical knowledge imparted in areas such as navigation.....",
    image: "https://via.placeholder.com/150x150?text=Nautical",
  },
  {
    title: "Diploma In Nautical Science",
    description:
      "One year Diploma in Nautical Science (DNS) is a pre-sea training course approved by Directorate General of Shipping (DGS) and affiliated to MIT Art Design Technology University Pune. The course is conducted at Maharashtra Academy of naval education and .....",
    image: "https://via.placeholder.com/150x150?text=DNS",
  },
  {
    title: "Electro Technical Officer",
    description:
      "Our ETO course is your pathway to a dynamic career at sea. Designed to meet the highest industry standards, this 17-week program provides the comprehensive training you need to excel as an Electro-Technical Officer (ETO). Gain hands-on expertise.....",
    image: "https://via.placeholder.com/150x150?text=ETO",
  },
];

// Custom arrows
function NextArrow(props) {
  const { onClick } = props;
  return (
    <div className="arrow next" onClick={onClick}>
      <ArrowForwardIos />
    </div>
  );
}

function PrevArrow(props) {
  const { onClick } = props;
  return (
    <div className="arrow prev" onClick={onClick}>
      <ArrowBackIos />
    </div>
  );
}

export default function Programs() {
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // desktop
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2, // tablet
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1, // mobile
        },
      },
    ],
  };

  return (
    <div className="programs-section">
      <div className="programs-title">
        <Typography variant="h4" component="h2" gutterBottom>
          Programs That Shape Futures
        </Typography>
        <Typography variant="subtitle1" color="textSecondary">
          From design to technology, our programs deliver measurable success
        </Typography>
      </div>

      <Slider {...settings}>
        {programsData.map((program, index) => (
          <div key={index} className="program-slide">
            <Card className="program-card">
              <div className="card-content">
                <img
                  src={program.image}
                  alt={program.title}
                  className="program-image"
                />
                <CardContent className="program-text">
                  <Typography variant="h6" className="program-title">
                    {program.title}
                  </Typography>
                  <Typography variant="body2" className="program-description">
                    {program.description}
                  </Typography>
                  <Button className="read-more">Read More</Button>
                </CardContent>
              </div>
            </Card>
          </div>
        ))}
      </Slider>
    </div>
  );
}
