import React from "react";
import "./Programs.css";
import {
  Grid,
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
} from "@mui/material";

const Programs = () => {
  // Sample data for the 4 cards
  const cardData = [
    { title: "Card 1", description: "Description for card 1." },
    { title: "Card 2", description: "Description for card 2." },
    { title: "Card 3", description: "Description for card 3." },
    { title: "Card 4", description: "Description for card 4." },
  ];

  return (
    <Grid
      container
      spacing={4}
      justifyContent="center"
      sx={{ padding: "40px 20px" }}
    >
      {cardData.map((card, index) => (
        <Grid
          item
          xs={12} // full width on extra small screens
          sm={6}  // half width on small screens
          md={
            index === 1 || index === 2 ? 4 : 2
          } // On medium+ screens: cards 2 & 3 wider (4/12 cols), cards 1 & 4 narrower (2/12 cols)
          key={index}
        >
          <Card
            sx={{
              // Make center cards taller
              height: index === 1 || index === 2 ? 320 : 250,
              display: "flex",
              flexDirection: "column",
              transition: "transform 0.3s, box-shadow 0.3s",
              "&:hover": {
                // Subtle hover effect
                transform: "translateY(-8px)",
                boxShadow: "0 8px 20px rgba(0,0,0,0.15)",
              },
            }}
          >
            {/* Card Content */}
            <CardContent sx={{ flexGrow: 1 }}>
              <Typography variant="h6" gutterBottom>
                {card.title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {card.description}
              </Typography>
            </CardContent>

            {/* Card Action (Button) */}
            <CardActions>
              <Button size="small" sx={{ color: "#d36408" }}>
                Learn More
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default Programs;
