import React, { useRef } from "react";
import "./alumni.css";

const alumniData = [
  {
    name: "John Doe",
    designation: "Software Engineer at Google",
    message: "The time at MANET helped me grow both technically and personally.",
    photo: "https://via.placeholder.com/100",
  },
  {
    name: "Jane Smith",
    designation: "Data Analyst at Microsoft",
    message: "The faculty and exposure I got here were incredible for my career.",
    photo: "https://via.placeholder.com/100",
  },
  {
    name: "Mark Wilson",
    designation: "DevOps Engineer at Amazon",
    message: "I’ll always be grateful for the guidance and friendships formed.",
    photo: "https://via.placeholder.com/100",
  },
  {
    name: "Lisa Brown",
    designation: "UI Designer at Adobe",
    message: "It was the most transformative phase of my journey.",
    photo: "https://via.placeholder.com/100",
  },
];

const Alumni = () => {
  const scrollRef = useRef(null);

  const scroll = (direction) => {
    const container = scrollRef.current;
    const cardWidth = container.querySelector(".alumni-card").offsetWidth + 20; // 20px gap
    container.scrollBy({
      left: direction === "left" ? -2 * cardWidth : 2 * cardWidth,
      behavior: "smooth",
    });
  };

  return (
    <section className="alumni-section">
      <h2 className="alumni-title">Our Alumni</h2>
      <p className="alumni-subtitle">
        Inspiring journeys from our proud graduates
      </p>

      <div className="alumni-carousel">
        <button className="scroll-btn left" onClick={() => scroll("left")}>
          &#8249;
        </button>

        <div className="alumni-cards-wrapper" ref={scrollRef}>
          {alumniData.map((alumni, index) => (
            <div className="alumni-card" key={index}>
              <div className="alumni-photo">
                <img src={alumni.photo} alt={alumni.name} />
              </div>
              <div className="alumni-details">
                <h3>{alumni.name}</h3>
                <p className="designation">{alumni.designation}</p>
                <p className="message">{alumni.message}</p>
              </div>
            </div>
          ))}
        </div>

        <button className="scroll-btn right" onClick={() => scroll("right")}>
          &#8250;
        </button>
      </div>
    </section>
  );
};

export default Alumni;
