import React, { useRef } from "react";
import "./placement.css";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

const students = [
  {
    name: "John Doe",
    program: "B.Tech CS",
    passoutYear: 2023,
    company: "Google",
    photo: "https://randomuser.me/api/portraits/men/1.jpg",
  },
  {
    name: "Jane Smith",
    program: "B.Tech IT",
    passoutYear: 2022,
    company: "Microsoft",
    photo: "https://randomuser.me/api/portraits/women/2.jpg",
  },
  {
    name: "Alice Johnson",
    program: "BBA",
    passoutYear: 2023,
    company: "Amazon",
    photo: "https://randomuser.me/api/portraits/women/3.jpg",
  },
];

const companies = [
  { name: "Google", logo: "https://logo.clearbit.com/google.com" },
  { name: "Microsoft", logo: "https://logo.clearbit.com/microsoft.com" },
  { name: "Amazon", logo: "https://logo.clearbit.com/amazon.com" },
  { name: "Facebook", logo: "https://logo.clearbit.com/facebook.com" },
  { name: "Apple", logo: "https://logo.clearbit.com/apple.com" },
];

const Placement = () => {
  const cardRef = useRef(null);
  const companyRef = useRef(null);

  const scrollLeft = (ref) => {
    ref.current.scrollBy({ left: -300, behavior: "smooth" });
  };

  const scrollRight = (ref) => {
    ref.current.scrollBy({ left: 300, behavior: "smooth" });
  };

  return (
    <div className="placement-section">
      {/* Section 1: Student Placement */}
      <div className="placement-students">
        <h2>Our Successful Placements</h2>
        <p>Meet our talented graduates placed in top companies</p>
        <div className="placement-card-wrapper">
          <button className="scroll-btn left" onClick={() => scrollLeft(cardRef)}>
            <FaArrowLeft />
          </button>
          <div className="placement-cards" ref={cardRef}>
            {students.map((student, index) => (
              <div className="placement-card" key={index}>
                <div className="card-left">
                  <h3>{student.name}</h3>
                  <p>Program: {student.program}</p>
                  <p>Passout: {student.passoutYear}</p>
                  <p>Company: {student.company}</p>
                </div>
                <div
                  className="card-right"
                  style={{
                    backgroundImage: `linear-gradient(to top, rgba(0,0,0,0.4), rgba(0,0,0,0.1)), url(${student.photo})`,
                  }}
                ></div>
              </div>
            ))}
          </div>
          <button className="scroll-btn right" onClick={() => scrollRight(cardRef)}>
            <FaArrowRight />
          </button>
        </div>
      </div>

      {/* Section 2: Companies */}
      <div className="placement-companies">
        <h2>Our Recruiting Companies</h2>
        <p>We have strong partnerships with top companies</p>
        <div className="company-wrapper">
          <div className="company-logos" ref={companyRef}>
            {companies.map((company, index) => (
              <div className="company-card" key={index}>
                <img src={company.logo} alt={company.name} />
                <p>{company.name}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Placement;
