import React, { useState, useEffect } from "react";
import axios from "axios";

function AddTask() {
  const [team, setTeam] = useState([]);
  const [tasks, setTasks] = useState([]);

  const [formData, setFormData] = useState({
    title: "",
    description: "",
    assigned_to: "",
    deadline: "",
  });

  // Fetch team and tasks on mount
  useEffect(() => {
    fetchTeam();
    fetchTasks();
  }, []);

  const fetchTeam = () => {
    axios.get("http://localhost:5000/team").then((res) => setTeam(res.data));
  };

  const fetchTasks = () => {
    axios.get("http://localhost:5000/tasks").then((res) => setTasks(res.data));
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/tasks", formData)
      .then((res) => {
        alert(res.data.message);
        setFormData({
          title: "",
          description: "",
          assigned_to: "",
          deadline: "",
        }); // Reset form
        fetchTasks(); // Refresh tasks list
      })
      .catch((err) => console.error(err));
  };

  return (
    <div className="add-task-form">
      {/* Add New Task */}
      <h2>Add New Task</h2>
      <form onSubmit={handleSubmit}>
        <label>Task Title:</label>
        <input type="text" name="title" onChange={handleChange} required />

        <label>Description:</label>
        <textarea name="description" onChange={handleChange} />

        <label>Assigned To:</label>
        <select name="assigned_to" onChange={handleChange} required>
          <option value="">Select Member</option>
          {team.map((member) => (
            <option key={member.id} value={member.id}>
              {member.name}
            </option>
          ))}
        </select>

        <label>Deadline:</label>
        <input type="date" name="deadline" onChange={handleChange} required />

        <button type="submit">Save Task</button>
      </form>

{/* Task List */}
      <h2>Task List</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {task.title} - Assigned to ID: {task.assigned_to} - Status: {task.status}
          </li>
        ))}
      </ul>

    </div>
  );
}

export default AddTask;
