import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
} from "@mui/material";
import "./Programs.css";

const Programs = () => {
  // Track the current center index
  const [centerIndex, setCenterIndex] = useState(0);
  const [slidesToShow, setSlidesToShow] = useState(4); // default desktop

  const cardData = [
    { title: "Card 1", description: "Description for card 1." },
    { title: "Card 2", description: "Description for card 2." },
    { title: "Card 3", description: "Description for card 3." },
    { title: "Card 4", description: "Description for card 4." },
    { title: "Card 5", description: "Description for card 5." },
    { title: "Card 6", description: "Description for card 6." },
  ];

  // Detect screen size
  useEffect(() => {
    const updateSlides = () => {
      if (window.innerWidth < 768) {
        setSlidesToShow(1); // Mobile
      } else if (window.innerWidth < 1200) {
        setSlidesToShow(3); // Tablet
      } else {
        setSlidesToShow(4); // Desktop
      }
    };
    updateSlides();
    window.addEventListener("resize", updateSlides);
    return () => window.removeEventListener("resize", updateSlides);
  }, []);

  // Slick carousel settings
  const settings = {
    dots: false,
    infinite: true,
    speed: 600,
    autoplay: true,
    autoplaySpeed: 2000,
    centerMode: true,
    centerPadding: "0px",
    slidesToShow: slidesToShow,
    beforeChange: (_, next) => setCenterIndex(next),
  };

  return (
    <div className="cards-container">
      <Slider {...settings}>
        {cardData.map((card, index) => {
          let isLarge = false;

          // Desktop (4 slides): make BOTH middle cards large
          if (slidesToShow === 4) {
            const secondCenter = (centerIndex + 1) % cardData.length;
            if (index === centerIndex || index === secondCenter) {
              isLarge = true;
            }
          }

          // Tablet (3 slides): only center card
          if (slidesToShow === 3) {
            if (index === centerIndex) {
              isLarge = true;
            }
          }

          // Mobile (1 slide): always large
          if (slidesToShow === 1) {
            isLarge = true;
          }

          return (
            <div key={index}>
              <Card className={`card ${isLarge ? "large" : ""}`}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    {card.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {card.description}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" sx={{ color: "#d36408" }}>
                    Learn More
                  </Button>
                </CardActions>
              </Card>
            </div>
          );
        })}
      </Slider>
    </div>
  );
};

export default Programs;
