import React from "react";
import Slider from "react-slick"; // Carousel library
import { Card, CardContent, Typography } from "@mui/material"; // Material UI components
import "./Programs.css"; // Custom CSS

// Array of programs to display
const programs = [
  { title: "Program 1", content: "Content for Program 1", image: "https://via.placeholder.com/150" },
  { title: "Program 2", content: "Content for Program 2", image: "https://via.placeholder.com/150" },
  { title: "Program 3", content: "Content for Program 3", image: "https://via.placeholder.com/150" },
  { title: "Program 4", content: "Content for Program 4", image: "https://via.placeholder.com/150" },
  { title: "Program 5", content: "Content for Program 5", image: "https://via.placeholder.com/150" },
];

const Programs = () => {
  // Slider settings for tablet & mobile
  const settings = {
    dots: true,          // Show dots only
    arrows: false,       // Remove prev/next arrows
    infinite: false,     // No infinite scroll
    speed: 500,          // Transition speed
    slidesToShow: 1,     // Default slides to show (mobile)
    slidesToScroll: 1,   // Scroll 1 card at a time
    responsive: [
      {
        breakpoint: 1024, // Tablet
        settings: {
          slidesToShow: 2, // Show 2 cards
          slidesToScroll: 1,
          dots: true,
          arrows: false,
        },
      },
      {
        breakpoint: 768, // Mobile
        settings: {
          slidesToShow: 1, // Show 1 card
          slidesToScroll: 1,
          dots: true,
          arrows: false,
        },
      },
    ],
  };

  return (
    <div className="programs-container">
      {/* Desktop view: show all cards horizontally */}
      <div className="desktop-cards">
        {programs.map((program, index) => (
          <Card key={index} className="program-card">
            <img src={program.image} alt={program.title} className="program-image" />
            <CardContent>
              <Typography variant="h6">{program.title}</Typography>
              <Typography variant="body2">{program.content}</Typography>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Tablet & Mobile carousel */}
      <div className="mobile-carousel">
        <Slider {...settings}>
          {programs.map((program, index) => (
            <Card key={index} className="program-card">
              <img src={program.image} alt={program.title} className="program-image" />
              <CardContent>
                <Typography variant="h6">{program.title}</Typography>
                <Typography variant="body2">{program.content}</Typography>
              </CardContent>
            </Card>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default Programs;
