import React, { useRef, useEffect, useState } from "react";
import "./Programs.css";

const cardsData = [
  { id: 1, title: "Program 1", desc: "Description 1" },
  { id: 2, title: "Program 2", desc: "Description 2" },
  { id: 3, title: "Program 3", desc: "Description 3" },
  { id: 4, title: "Program 4", desc: "Description 4" },
  { id: 5, title: "Program 5", desc: "Description 5" },
  { id: 6, title: "Program 6", desc: "Description 6" },
];

const Programs = () => {
  const containerRef = useRef(null);
  const [visibleCards, setVisibleCards] = useState(4);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth >= 1024) setVisibleCards(4); // desktop
      else if (window.innerWidth >= 768) setVisibleCards(2); // tablet
      else setVisibleCards(1); // mobile
    };
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    const container = containerRef.current;
    let scrollAmount = 0;

    const scrollCards = () => {
      scrollAmount += 1;
      if (scrollAmount >= container.scrollWidth / 2) scrollAmount = 0;
      container.scrollTo({ left: scrollAmount, behavior: "smooth" });
      requestAnimationFrame(scrollCards);
    };

    requestAnimationFrame(scrollCards);
  }, []);

  return (
    <div className="programs-carousel" ref={containerRef}>
      {cardsData.concat(cardsData).map((card, index) => {
        const centerIndex = Math.floor(visibleCards / 2);
        return (
          <div
            key={index}
            className={`program-card ${
              index % cardsData.length >= centerIndex &&
              index % cardsData.length < centerIndex + (visibleCards === 4 ? 2 : 1)
                ? "center-card"
                : ""
            }`}
          >
            <h3>{card.title}</h3>
            <p>{card.desc}</p>
          </div>
        );
      })}
    </div>
  );
};

export default Programs;
