import React from "react";
import { FaArrowRight } from "react-icons/fa";
import "./LifeAtManet.css";
import { Link } from "react-router-dom";

const LifeAtManet = () => {
  const cards = [
    {
      title: "Card 1",
      buttons: [
  { text: "Explore", link: "/explore" },
  { text: "Join Us", link: "/join" },
  { text: "Learn More", link: "/learn" },
]
    },
    {
      title: "Card 2",
    buttons: [
  { text: "Explore", link: "/explore" },
  { text: "Join Us", link: "/join" },
  { text: "Learn More", link: "/learn" },
]
    },
    {
      title: "Card 3",
     buttons: [
  { text: "Explore", link: "/explore" },
  { text: "Join Us", link: "/join" },
  { text: "Learn More", link: "/learn" },
]
    },
  ];

  return (
    <section className="lifeatmanet-section">
      <div className="container" id="lifeatmanet-container">
        <h2 className="lifeatmanet-title">Life at Manet</h2>
        <p className="lifeatmanet-subtitle">Experience the culture and opportunities</p>

        <div className="lifeatmanet-cards-container">
          {cards.map((card, idx) => (
            <div key={idx} className="lifeatmanet-card">
              <h3>{card.title}</h3>
              <div className="lifeatmanet-btn-container">
                // Inside the card buttons mapping
{card.buttons.map((btn, i) => (
  <Link to={btn.link} key={i}>
    <button className="lifeatmanet-card-button">
      <FaArrowRight className="arrow-icon" />
      <span>{btn.text}</span>
    </button>
  </Link>
))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default LifeAtManet;
