import React, { useState, useEffect } from "react";
import axios from "axios";

function AddTask() {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    assigned_to: "",
    deadline: "",
  });

  const [team, setTeam] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/team").then((res) => {
      setTeam(res.data);
    });
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/tasks", formData)
      .then((res) => {
        alert(res.data.message);
        fetchTasks(); // <-- Refresh the task list after adding
      })
      .catch((err) => console.error(err));
  };

  return (
    <div className="add-task-form">
      <h2>Add New Task</h2>
      <form onSubmit={handleSubmit}>
        <label>Task Title:</label>
        <input type="text" name="title" onChange={handleChange} required />

        <label>Description:</label>
        <textarea name="description" onChange={handleChange} />

        <label>Assigned To:</label>
        <select name="assigned_to" onChange={handleChange} required>
          <option value="">Select Member</option>
          {team.map((member) => (
            <option key={member.id} value={member.id}>
              {member.name}
            </option>
          ))}
        </select>

        <label>Deadline:</label>
        <input type="date" name="deadline" onChange={handleChange} required />

        <button type="submit">Save Task</button>
      </form>
    </div>
  );
}

export default AddTask;
