import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const [formData, setFormData] = useState({email:"", password:""});
  const navigate = useNavigate();

  const handleChange = (e)=>setFormData({...formData, [e.target.name]: e.target.value});

  const handleSubmit = (e)=>{
    e.preventDefault();
    axios.post("http://localhost:5000/users/login", formData, {withCredentials:true})
      .then(res=>{
        alert(res.data.message);
        navigate("/dashboard");
      })
      .catch(err=>alert(err.response?.data?.message || "Login failed"));
  };

  return (
    <div style={{maxWidth:"400px", margin:"50px auto"}}>
      <h2>Admin Login</h2>
      <form onSubmit={handleSubmit}>
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required style={{width:"100%", padding:"8px", marginBottom:"10px"}} />
        <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required style={{width:"100%", padding:"8px", marginBottom:"10px"}} />
        <button type="submit" style={{padding:"10px 20px"}}>Login</button>
      </form>
    </div>
  );
}

export default Login;
