import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import { Card, CardContent, Typography, CardMedia } from "@mui/material";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Programs.css";

const cardsData = [
  { id: 1, title: "Program 1", desc: "Description 1", image: "https://source.unsplash.com/400x250/?nature,water" },
  { id: 2, title: "Program 2", desc: "Description 2", image: "https://source.unsplash.com/400x250/?nature,forest" },
  { id: 3, title: "Program 3", desc: "Description 3", image: "https://source.unsplash.com/400x250/?nature,mountain" },
  { id: 4, title: "Program 4", desc: "Description 4", image: "https://source.unsplash.com/400x250/?nature,beach" },
  { id: 5, title: "Program 5", desc: "Description 5", image: "https://source.unsplash.com/400x250/?nature,river" },
];

const Programs = () => {
  const [centerIndex, setCenterIndex] = useState(0);
  const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 1025);

  useEffect(() => {
    const handleResize = () => setIsDesktop(window.innerWidth >= 1025);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    centerMode: true,
    centerPadding: "0px",
    autoplay: true,
    autoplaySpeed: 2500,
    dots: true,
    afterChange: (current) => {
      if (isDesktop) setCenterIndex(current % cardsData.length);
    },
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 2, centerMode: false } },
      { breakpoint: 768, settings: { slidesToShow: 1, centerMode: false } },
    ],
  };

  return (
    <div className="programs-section">
      <div className="programs-title">
        <h1>Our Programs</h1>
        <h2>Explore the variety of courses we offer</h2>
      </div>

      <Slider {...settings} className="programs-slider">
        {cardsData.map((card, index) => {
          let wrapperClass = "program-card-wrapper";

          // Add class to the two center cards dynamically
          if (isDesktop) {
            if (index === centerIndex) wrapperClass += " first-center";
            if (index === (centerIndex + 1) % cardsData.length) wrapperClass += " second-center";
          }

          return (
            <div key={card.id} className={wrapperClass}>
              <Card className="program-card" elevation={3}>
                <CardMedia
                  component="img"
                  image={card.image}
                  alt={card.title}
                  className="program-card-image"
                />
                <CardContent className="program-card-content">
                  <div className="gradient-overlay" />
                  <Typography variant="h6">{card.title}</Typography>
                  <Typography variant="body2">{card.desc}</Typography>
                </CardContent>
              </Card>
            </div>
          );
        })}
      </Slider>
    </div>
  );
};

export default Programs;
