import React from "react";
import Slider from "react-slick";
import { Card, CardContent, Typography, Button } from "@mui/material";
import "./Programs.css";

// Sample card data
const programs = [
  {
    title: "B. Tech. Marine Engineering",
    description: "The Department of Marine Engineering aims to train the best marine engineering graduates for the industry. Along with providing a broad-based knowledge in theory, the students are exposed to marine equipment with visit to ships and repair yards.....",
    image: "/mnt/data/543480ca-2caf-4e2f-962b-83c01b4519cd.png",
  },
  {
    title: "B. Sc. Nautical Science",
    description: "The Department of Nautical Science enables graduates to become Navigating Officers on-board merchant ships plying worldwide. The bachelor program deals with theoretical and practical knowledge imparted in navigation.....",
    image: "/mnt/data/543480ca-2caf-4e2f-962b-83c01b4519cd.png",
  },
  {
    title: "Diploma In Nautical Science",
    description: "One year Diploma in Nautical Science (DNS) is a pre-sea training course approved by Directorate General of Shipping (DGS) and affiliated to MIT Art Design Technology University Pune. The course is conducted at Maharashtra Academy of naval education and .....",
    image: "/mnt/data/543480ca-2caf-4e2f-962b-83c01b4519cd.png",
  },
  {
    title: "Electro Technical Officer",
    description: "Our ETO course is your pathway to a dynamic career at sea. Designed to meet the highest industry standards, this 17-week program provides comprehensive training to excel as an Electro-Technical Officer (ETO). Gain hands-on expertise.....",
    image: "/mnt/data/543480ca-2caf-4e2f-962b-83c01b4519cd.png",
  },
];

const Programs = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024, // Tablet
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 768, // Mobile
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
    ],
  };

  return (
    <div className="programs-container">
      <Typography variant="h4" align="center" gutterBottom>
        Programs That Shape Futures
      </Typography>
      <Typography variant="subtitle1" align="center" gutterBottom>
        From design to technology, our programs deliver measurable success
      </Typography>
      <Slider {...settings}>
   {programs.map((program, index) => (
  <Card className="program-card" key={index}>
    {/* Image on top */}
    <div className="card-left">
      <img src={program.image} alt={program.title} />
    </div>

    {/* Centered content */}
    <div className="card-center">
      <Typography className="card-title" variant="h6">
        {program.title}
      </Typography>

      <CardContent className="card-description">
        <Typography variant="body2">{program.description}</Typography>
        <Button className="read-more" variant="text">
          Read More
        </Button>
      </CardContent>
    </div>
  </Card>
))}

      </Slider>
    </div>
  );
};

export default Programs;
