import React from "react";
import Slider from "react-slick";

import { Card, CardContent, Typography } from "@mui/material";
import "./Programs.css";

const Programs = () => {
  // Example card data
  const cardData = [
    {
      image: "/images/eto.jpg",
      title: "Electro Technical Officer",
      description: "(4 months)",
    },
    {
      image: "/images/btech.jpg",
      title: "B. Tech Marine Engineering",
      description: "(4 years)",
    },
    {
      image: "/images/bsc.jpg",
      title: "B.Sc. Nautical Science",
      description: "(3 years)",
    },
    {
      image: "/images/dns.jpg",
      title: "Diploma in Nautical Science",
      description: "(1 year)",
    },
    {
      image: "/images/marine.jpg",
      title: "Marine Engineering",
      description: "(2 years)",
    },
  ];

  // Slick settings
 const settings = {
  dots: true,
  infinite: true,
  centerMode: true,
  slidesToShow: 4,
  slidesToScroll: 1,
  autoplay: true,
  autoplaySpeed: 0,       // ðŸ”‘ no delay, continuous
  speed: 6000,            // ðŸ”‘ higher = smoother, slower movement
  cssEase: "linear",      // ðŸ”‘ makes it continuous
  arrows: false,          // no arrows
  responsive: [
    {
      breakpoint: 1200,
      settings: { slidesToShow: 3, centerMode: true }
    },
    {
      breakpoint: 768,
      settings: { slidesToShow: 1, centerMode: true }
    }
  ]
};


  return (
    <div className="programs-section">
      <h2 className="section-title">
        Comprehensive Programs For <br />
        <span className="highlight">Holistic Learning</span>
      </h2>

      <Slider {...settings}>
        {cardData.map((card, index) => (
          <div key={index} className="card-wrapper">
            <Card className="program-card">
              <div
                className="card-image"
                style={{ backgroundImage: `url(${card.image})` }}
              />
              <CardContent className="card-overlay">
                <Typography variant="h6" className="card-title">
                  {card.title}
                </Typography>
                <Typography variant="body2" className="card-description">
                  {card.description}
                </Typography>
              </CardContent>
            </Card>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Programs;
