import React from "react";
import "./Programs.css"; // Import the CSS file for layout + responsive styles
import {
  Grid,
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
} from "@mui/material";

const Programs = () => {
  // Array holding the data for each card
  const cardData = [
    { title: "Card 1", description: "Description for card 1." },
    { title: "Card 2", description: "Description for card 2." },
    { title: "Card 3", description: "Description for card 3." },
    { title: "Card 4", description: "Description for card 4." },
  ];

  return (
    // Wrapper div uses CSS grid rules from Programs.css
    <div className="cards-container">
      {/* MUI Grid container for spacing & alignment */}
      <Grid container spacing={4} justifyContent="center">
        {cardData.map((card, index) => (
          // Each card is inside a Grid item
          <Grid item xs={12} sm={6} md={3} key={index}>
            {/* Card with custom "card" class for styling in CSS */}
            <Card className="card">
              {/* Card body content */}
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {card.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {card.description}
                </Typography>
              </CardContent>

              {/* Card actions (button at bottom) */}
              <CardActions>
                <Button size="small" sx={{ color: "#d36408" }}>
                  Learn More
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default Programs;
