import React, { useEffect, useState } from "react";
import axios from "axios";

function TaskList() {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = () => {
    axios
      .get("http://localhost:8081/tasks")
      .then((res) => setTasks(res.data))
      .catch((err) => console.error("Error fetching tasks:", err));
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this task?")) {
      axios
        .delete(`http://localhost:8081/tasks/${id}`)
        .then(() => fetchTasks());
    }
  };

  const markComplete = (id) => {
    axios.patch(`http://localhost:8081/tasks/${id}/complete`)
      .then(() => fetchTasks());
  };

  return (
    <div className="task-list">
      <h2>Task List</h2>
      <table border="1" cellPadding="8" cellSpacing="0">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Assigned To</th>
            <th>Deadline</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.title}</td>
              <td>{task.assigned_name || "Unassigned"}</td>
              <td>{task.deadline}</td>
              <td>{task.status}</td>
              <td>
                <button onClick={() => markComplete(task.id)}>‚úîÔ∏è Complete</button>
                <button onClick={() => handleDelete(task.id)}>üóëÔ∏è Delete</button>
                {/* For edit you can navigate to edit page */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TaskList;
