import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import { Card, CardContent, Typography, CardMedia } from "@mui/material";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Programs.css";

const cardsData = [
  { id: 1, title: "Program 1", desc: "Description 1", image: "https://source.unsplash.com/400x250/?nature,water" },
  { id: 2, title: "Program 2", desc: "Description 2", image: "https://source.unsplash.com/400x250/?nature,forest" },
  { id: 3, title: "Program 3", desc: "Description 3", image: "https://source.unsplash.com/400x250/?nature,mountain" },
  { id: 4, title: "Program 4", desc: "Description 4", image: "https://source.unsplash.com/400x250/?nature,beach" },
];

const Programs = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: windowWidth >= 1025 ? 4 : windowWidth >= 768 ? 2 : 1,
    slidesToScroll: 1,
    centerMode: false,
    autoplay: true,
    autoplaySpeed: 2500,
    dots: true, // Enable dots
  };

  return (
    <div className="programs-section">
      <div className="programs-title">
        <h1>Our Programs</h1>
        <h2>Explore the variety of courses we offer</h2>
      </div>

      <Slider {...settings} className="programs-slider">
        {cardsData.map((card, index) => {
          let scaleClass = "scale-wrapper";

          // Desktop: enlarge middle two cards
          if (windowWidth >= 1025) {
            if (index === 1 || index === 2) scaleClass += " center-card";
            else scaleClass += " small-card";
          }

          return (
            <div key={card.id} className="program-card-wrapper">
              {/* Extra wrapper for scaling transition */}
              <div className={scaleClass}>
                <Card className="program-card" elevation={3}>
                  <CardMedia
                    component="img"
                    image={card.image}
                    alt={card.title}
                    className="program-card-image"
                  />
                  <div className="gradient-overlay"></div>
                  <CardContent className="program-card-content">
                    <Typography variant="h6">{card.title}</Typography>
                    <Typography variant="body2">{card.desc}</Typography>
                  </CardContent>
                </Card>
              </div>
            </div>
          );
        })}
      </Slider>
    </div>
  );
};

export default Programs;
