import React, { useState } from "react";
import "./AlumniSpeak.css";

const AlumniSpeak = () => {
  const alumni = [
    {
      name: "John Doe",
      designation: "Software Engineer at Google",
      photo: "https://via.placeholder.com/100",
      message:
        "MANET helped me grow professionally and personally. The faculty support is excellent!",
    },
    {
      name: "Jane Smith",
      designation: "Data Analyst at Amazon",
      photo: "https://via.placeholder.com/100",
      message:
        "Life at MANET gave me exposure to real-world projects and amazing peer collaboration.",
    },
    {
      name: "Alex Johnson",
      designation: "Product Manager at Microsoft",
      photo: "https://via.placeholder.com/100",
      message:
        "The college prepared me thoroughly for industry challenges and leadership roles.",
    },
    {
      name: "Emily Davis",
      designation: "UX Designer at Facebook",
      photo: "https://via.placeholder.com/100",
      message:
        "Wonderful environment and learning opportunities. Highly recommend MANET to future students.",
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const isMobile = window.innerWidth <= 768;
  const cardsToShow = isMobile ? 1 : 2;

  const prev = () => {
    setCurrentIndex((prevIndex) =>
      Math.max(prevIndex - cardsToShow, 0)
    );
  };

  const next = () => {
    setCurrentIndex((prevIndex) =>
      Math.min(prevIndex + cardsToShow, alumni.length - cardsToShow)
    );
  };

  return (
    <section className="alumni-section">
      <div className="alumni-container">
        <h2 className="alumni-title">Alumni Speak</h2>
        <p className="alumni-subtitle">
          Hear from our successful alumni about their journey.
        </p>

        <div className="alumni-carousel">
          <button className="scroll-btn left" onClick={prev}>
            &#8249;
          </button>

          <div className="alumni-cards-wrapper">
            <div
              className="alumni-cards"
              style={{
                transform: `translateX(-${(currentIndex / alumni.length) *
                  100}%)`,
                width: `${(alumni.length / cardsToShow) * 100}%`,
              }}
            >
              {alumni.map((alum, idx) => (
                <div
                  className="alumni-card"
                  key={idx}
                  style={{ width: `${100 / alumni.length}%` }}
                >
                  <div className="alumni-photo">
                    <img src={alum.photo} alt={alum.name} />
                  </div>
                  <div className="alumni-details">
                    <h3>{alum.name}</h3>
                    <p className="designation">{alum.designation}</p>
                    <p className="message">{alum.message}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <button className="scroll-btn right" onClick={next}>
            &#8250;
          </button>
        </div>

        {/* Dots */}
        <div className="alumni-dots">
          {alumni.map((_, idx) => (
            <span
              key={idx}
              className={`dot ${
                idx >= currentIndex && idx < currentIndex + cardsToShow
                  ? "active"
                  : ""
              }`}
            ></span>
          ))}
        </div>
      </div>
    </section>
  );
};

export default AlumniSpeak;
