import React from "react";
import Slider from "react-slick"; // Carousel library
import { Card, CardContent, Typography } from "@mui/material"; // Material UI components
import "./Programs.css"; // Custom CSS

// Array of programs to display
const programs = [
  {
    title: "Program 1",
    content: "Content for Program 1",
    image: "https://via.placeholder.com/150",
  },
  {
    title: "Program 2",
    content: "Content for Program 2",
    image: "https://via.placeholder.com/150",
  },
  {
    title: "Program 3",
    content: "Content for Program 3",
    image: "https://via.placeholder.com/150",
  },
  {
    title: "Program 4",
    content: "Content for Program 4",
    image: "https://via.placeholder.com/150",
  }
];

const Programs = () => {
  // Slider settings for react-slick
  const settings = {
    dots: true, // Show dot navigation
    arrows: false, // Remove prev/next arrows
    infinite: false, // No infinite scroll
    speed: 500, // Transition speed in ms
    slidesToShow: 4, // Desktop: show 4 cards
    slidesToScroll: 1, // Scroll 1 card at a time
    responsive: [
      {
        breakpoint: 1024, // Tablet view
        settings: {
          slidesToShow: 2, // Show 2 cards
          slidesToScroll: 1,
          dots: true, // Keep dot navigation
          arrows: false, // Remove arrows
        },
      },
      {
        breakpoint: 768, // Mobile view
        settings: {
          slidesToShow: 1, // Show 1 card
          slidesToScroll: 1,
          dots: true, // Dot navigation
          arrows: false, // Remove arrows
        },
      },
    ],
  };

  return (
    <div className="programs-container">
      {/* React Slick Carousel */}
      <Slider {...settings}>
        {programs.map((program, index) => (
          <Card key={index} className="program-card">
            {/* Program image */}
            <img src={program.image} alt={program.title} className="program-image" />
            
            {/* Program title and content */}
            <CardContent>
              <Typography variant="h6">{program.title}</Typography>
              <Typography variant="body2">{program.content}</Typography>
            </CardContent>
          </Card>
        ))}
      </Slider>
    </div>
  );
};

export default Programs;
